import '../css/availability.css';
import '../css/calendar.css'
import icon_search from '../img/icon_search2.png';
import { useState, useEffect } from 'react';
import CalendarComponent from './calendar.js';

export default function CheckAvailability() {
    const [selectedStartDate, setSelectedStartDate] = useState(null);
    const [selectedEndDate, setSelectedEndDate] = useState(null);
    const [isStartCalendarVisible, setStartCalendarVisible] = useState(false);
    const [isEndCalendarVisible, setEndCalendarVisible] = useState(false);
  
    const handleStartDateChange = (date) => {
      setSelectedStartDate(date);
    };
  
    const handleEndDateChange = (date) => {
      setSelectedEndDate(date);
    };
  
    const openStartCalendar = () => {
      setStartCalendarVisible(!isStartCalendarVisible);
    };
  
    const openEndCalendar = () => {
      setEndCalendarVisible(!isEndCalendarVisible);
    };
  
    useEffect(() => {
      const bookContainer = document.querySelector('.book--container');
      const startCalendar = document.querySelector('.start-react-calendar');
      const endCalendar = document.querySelector('.end-react-calendar');
  
      if (bookContainer && startCalendar && endCalendar) {
        bookContainer.classList.add('book--container-visible');
        startCalendar.classList.toggle('hidden', !isStartCalendarVisible);
        endCalendar.classList.toggle('hidden', !isEndCalendarVisible);
      }
    }, [isStartCalendarVisible, isEndCalendarVisible]);
  
    return (
      <div className="book--container" id="book">
        <h2>Check room availability now!</h2>
        <div className="form--book">
          <label htmlFor="" className="form--hotel_choose">
            Choose your hotel:
          </label>
          <select name="hotel" className="form--select">
            <option value="OldTown">Old Town Hotel</option>
            <option value="Cracow">Cracow Hotel</option>
            <option value="Wawel">Wawel Hotel</option>
            <option value="Station">Main Station Hotel</option>
          </select>
          <label>From: </label>
          <div className="choose--date date--start" onClick={openStartCalendar}>
            <p>{selectedStartDate ? selectedStartDate.toLocaleDateString() : 'dd.mm.yyyy'}</p>
          </div>
          <label>To: </label>
          <div className="choose--date date--end" onClick={openEndCalendar}>
            <p>{selectedEndDate ? selectedEndDate.toLocaleDateString() : 'dd.mm.yyyy'}</p>
          </div>
          {isStartCalendarVisible && (
            <div className="start-react-calendar">
              <CalendarComponent onDateChange={handleStartDateChange} />
            </div>
          )}
          {isEndCalendarVisible && (
            <div className="end-react-calendar">
              <CalendarComponent onDateChange={handleEndDateChange} />
            </div>
          )}
          <button className="form--submit">
            <img src={icon_search} alt="search" />
          </button>
        </div>
      </div>
    );
  }
