import '../css/availability.css';
import '../css/calendar.css'
import icon_search from '../img/icon_search2.png';
import { useState, useEffect, useRef } from 'react';
import CalendarComponent from './calendar.js';

export default function CheckAvailability() {
    const [selectedDate, setSelectedDate] = useState(null);
    const [isCalendarVisible, setCalendarVisible] = useState(false);
    const calendarRef = useRef(null);
  
    const handleDateChange = (date) => {
      setSelectedDate(date);
    };
  
    const openCalendar = () => {
      setCalendarVisible(!isCalendarVisible);
    };
  
    const handleClickOutsideCalendar = (event) => {
      if (calendarRef.current && !calendarRef.current.contains(event.target)) {
        setCalendarVisible(false);
      }
    };
  
    useEffect(() => {
      const bookContainer = document.querySelector('.book--container');
      const calendar = document.querySelector('.react-calendar');
      bookContainer.classList.add('book--container-visible');
      calendar.classList.add('hidden');
  
      document.addEventListener('click', handleClickOutsideCalendar);
  
      return () => {
        document.removeEventListener('click', handleClickOutsideCalendar);
      };
    }, []);
  
    return (
      <div className="book--container" id="book">
        <h2>Check room availability now!</h2>
        <div className="form--book">
          <label htmlFor="" className="form--hotel_choose">
            Choose your hotel:
          </label>
          <select name="hotel" className="form--select">
            <option value="OldTown">Old Town Hotel</option>
            <option value="Cracow">Cracow Hotel</option>
            <option value="Wawel">Wawel Hotel</option>
            <option value="Station">Main Station Hotel</option>
          </select>
          <label>From: </label>
          <div className="choose--date date--start" onClick={openCalendar}>
            <p>{selectedDate ? selectedDate.toLocaleDateString() : 'dd.mm.yyyy'}</p>
          </div>
          <label>To: </label>
          <div className="choose--date date--end" onClick={openCalendar}>
            <p>{selectedDate ? selectedDate.toLocaleDateString() : 'dd.mm.yyyy'}</p>
          </div>
          {isCalendarVisible && (
            <div ref={calendarRef}>
              <CalendarComponent onDateChange={handleDateChange} />
            </div>
          )}
          <button className="form--submit">
            <img src={icon_search} alt="search" />
          </button>
        </div>
      </div>
    );
  }
