import '../css/availability.css';
import '../css/calendar.css'
import icon_search from '../img/icon_search2.png';
import { useState, useEffect, useRef } from 'react';
import CalendarComponent from './calendar.js';

export default function CheckAvailability() {
  const [selectedStartDate, setSelectedStartDate] = useState(null);
  const [selectedEndDate, setSelectedEndDate] = useState(null);
  const [isCalendarVisible, setCalendarVisible] = useState({ start: false, end: false });
  const calendarRef = useRef(null);

  const handleDateChange = (date, isStartDate) => {
    if (isStartDate) {
      setSelectedStartDate(date);
    } else {
      setSelectedEndDate(date);
    }
  };

  const openCalendar = (calendarType) => {
    setCalendarVisible((prev) => ({ ...prev, [calendarType]: !prev[calendarType] }));
  };

  const handleClickOutsideCalendar = (event) => {
    if (calendarRef.current && !calendarRef.current.contains(event.target)) {
      setCalendarVisible({ start: false, end: false });
    }
  };

  useEffect(() => {
    const bookContainer = document.querySelector('.book--container');
    bookContainer.classList.add('book--container-visible');

    document.addEventListener('click', handleClickOutsideCalendar);

    return () => {
      document.removeEventListener('click', handleClickOutsideCalendar);
    };
  }, []);

  return (
    <div className="book--container" id="book">
      <h2>Check room availability now!</h2>
      <div className="form--book">
        <label htmlFor="" className="form--hotel_choose">
          Choose your hotel:
        </label>
        <select name="hotel" className="form--select">
          <option value="OldTown">Old Town Hotel</option>
          <option value="Cracow">Cracow Hotel</option>
          <option value="Wawel">Wawel Hotel</option>
          <option value="Station">Main Station Hotel</option>
        </select>
        <label>From: </label>
        <div className="choose--date date--start" onClick={() => openCalendar('start')}>
          <p>{selectedStartDate ? selectedStartDate.toLocaleDateString() : 'dd.mm.yyyy'}</p>
        </div>
        <label>To: </label>
        <div className="choose--date date--end" onClick={() => openCalendar('end')}>
          <p>{selectedEndDate ? selectedEndDate.toLocaleDateString() : 'dd.mm.yyyy'}</p>
        </div>
        {isCalendarVisible.start && (
          <div className="calendar--start" ref={calendarRef}>
            <CalendarComponent onDateChange={(date) => handleDateChange(date, true)} />
          </div>
        )}
        {isCalendarVisible.end && (
          <div className="calendar--end" ref={calendarRef}>
            <CalendarComponent onDateChange={(date) => handleDateChange(date, false)} />
          </div>
        )}
        <button className="form--submit">
          <img src={icon_search} alt="search" />
        </button>
      </div>
    </div>
  );
        }
