import React, { useState, useEffect } from "react";
import PropTypes from 'prop-types';
import '../css/spinner.css';
import icon_loading from '../img/icon_loading.png';

function Spinner({ timeoutDuration }) {
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        const showSpinner = () => setIsVisible(true);
        const hideSpinner = () => setIsVisible(false);

        showSpinner();

        const timer = setTimeout(() => {
            hideSpinner();
        }, timeoutDuration);

        return () => {
            clearTimeout(timer);
        };
    }, [timeoutDuration]);

    return (
        <div className={`spinner ${isVisible ? '' : 'hidden'}`}>
            <img src={icon_loading} alt="Loading"></img>
        </div>
    );
}

Spinner.propTypes = {
    timeoutDuration: PropTypes.number.isRequired,
};

export default Spinner;
